body.script:
$('#username').focus()
$('#password').bind('keyup', function(event) {
    if(event.keyCode==13){
       $('#form').submit();    
    }
});
$('#form').submit(function (e) {
    e.preventDefault();
    $('#login').addClass('disabled');
    Genesis.UI.wipeOut('visible'); 
    $.ajax({
        url: '/auth',
        type: 'POST',
        data: {
            username: $('#username').val(),
            response: $('#password').val()
        },
        success: function () {
            $('#form').unbind('submit');
            $('#password').val('');
            $('#login').click();
        }
    });
});
